if (!context.global.medications) {
    const fs = require('fs');
    const data = fs.readFileSync('/data/medications-100.json', 'utf8');
    context.global.medications = JSON.parse(data);
}
msg.payload = context.global.medications;
msg.statusCode = 200;
return msg;

const medications = context.global.medications || [];
const id = msg.req.params.id;
const medication = medications.find(m => m.id === id);
if (medication) {
    msg.payload = medication;
    msg.statusCode = 200;
} else {
    msg.payload = { error: 'Medication not found' };
    msg.statusCode = 404;
}
return msg;

const medications = context.global.medications || [];
const newMedication = msg.payload;
if (!newMedication.id) {
    newMedication.id = String(Date.now());
}
if (!newMedication.name || !newMedication.dosage || !newMedication.frequency) {
    msg.payload = { error: 'Missing required fields' };
    msg.statusCode = 400;
    return msg;
}
medications.push(newMedication);
context.global.medications = medications;
msg.payload = newMedication;
msg.statusCode = 201;
return msg;

const medications = context.global.medications || [];
const id = msg.req.params.id;
const updates = msg.payload;
const index = medications.findIndex(m => m.id === id);
if (index !== -1) {
    medications[index] = { ...medications[index], ...updates, id: medications[index].id };
    context.global.medications = medications;
    msg.payload = medications[index];
    msg.statusCode = 200;
} else {
    msg.payload = { error: 'Medication not found' };
    msg.statusCode = 404;
}
return msg;

const medications = context.global.medications || [];
const id = msg.req.params.id;
const index = medications.findIndex(m => m.id === id);
if (index !== -1) {
    const deleted = medications.splice(index, 1)[0];
    context.global.medications = medications;
    msg.payload = { message: 'Medication deleted successfully', deleted: deleted };
    msg.statusCode = 200;
} else {
    msg.payload = { error: 'Medication not found' };
    msg.statusCode = 404;
}
return msg;
